
public class McAdooHelloWorld {

	public static void main(String[] args) {
		 // TODO Auto-generated method stub
	    //System.out.println("Hello World!");
	    //System.out.println("This is my first program");
		
		//use any type of variables to describe a number while computer solves math equations 
		int x = 2;
		int y = 6;
		int b = -4;
		int m = (y-b)/x;
		
		//This is the x-value of my slope-intercept formula
		System.out.print("The x-value is: ");
		System.out.println(x);
		
		//This is the y-value of my slope-intercept formula
		System.out.print("The y-value is: ");
		System.out.println(y);
		
		//This is the y-intercept of my slope-intercept formula
		System.out.print("The y-intercept is: ");
		System.out.println(b);
		
		//This is the calculated slope in my equation
		System.out.print("The slope is: ");
		System.out.println(m);

	}

}
